SELECT "Creating new data_option_subcategory table" AS "";

CREATE TABLE IF NOT EXISTS data_option_subcategory (
  id INT UNSIGNED NOT NULL AUTO_INCREMENT,
  update_timestamp TIMESTAMP NOT NULL,
  create_timestamp TIMESTAMP NOT NULL,
  type ENUM('baseline', 'mcq', 'physical', 'biomarker') NOT NULL,
  rank INT UNSIGNED NOT NULL,
  data_option_parent_id INT UNSIGNED NULL,
  name_en VARCHAR(63) NOT NULL,
  name_fr VARCHAR(63) NOT NULL,
  note_en VARCHAR(1023) NULL,
  note_fr VARCHAR(1023) NULL,
  PRIMARY KEY (id),
  INDEX fk_data_option_parent_id (data_option_parent_id ASC),
  UNIQUE INDEX uq_type_rank (type ASC, rank ASC),
  CONSTRAINT fk_data_option_subcategory_data_option_parent_id
    FOREIGN KEY (data_option_parent_id)
    REFERENCES data_option_parent (id)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;

